function Skill(d3class, levelReq, skillName, skillText, runeName, runeText){
 this.d3class = d3class;
 this.levelReq = levelReq;
 this.skillName = skillName;
 this.skillText = skillText;
 this.runeName = runeName;
 this.runeText = runeText;
}

function Passive(d3class, levelReq, passiveName, passiveText){
 this.d3class = d3class;
 this.levelReq = levelReq;
 this.passiveName = passiveName;
 this.passiveText = passiveText;
}

